import React, { useState } from 'react';
import Search from './Search'
import axios from 'axios'

import Results from './Results'
import Popup from './Popup'


function TabSearchContent(){

    const [state, setState] = useState({
        s: "",
        results: [],
        selected: {}
      }); 
    const apiurl = "https://api.spotify.com/v1/search?"

    const search = (e) => {

        const auth_token = "BQDhAQt0DpEoTO8btbACc195Z_294FfGRiJfyTULZ3UhyU-MHuS5CX-SDl_HYbfqg-HLMPMGxzdl-Lt8uEIJPTtiYOL7-C72tPuS4di5fZorQvqW_iX8QUmFH6HfNqoJ7JkSlPzdJJAoSDgVh74hWpeRm4B7cPQ7UHucgQx4lbuJmjCvG0CyqZefyCBiFvUlDCs7hsBAB5R-_k2b";
        let config = {
            headers: {
                Authorization: 'Bearer '+ auth_token
            }
        }
        if (e.key == "Enter"){// user pressed enter, return the result
            // api request

            axios(apiurl + "q=" + state.s + "&type=album" + "&limit=20", headers).then(({ data }) => {
            let results = data.Search;
            setState(prevState => {
                return { ... prevState, results: results}
            })
            });
        }
    }
    const handleInput = (e) => {
    let s = e.target.value;

    setState(prevState => {
        return { ...prevState, s: s}
    });

    }

    // shows detail page 
    const openPopup = id => {
    axios(apiurl + "&i=" + id).then(({ data }) => {
        let result = data;

        setState(prevState => {
        return { ... prevState, selected: result}
        });
    });
    }

    const closePopup = () => {
    setState(prevState => {
        return { ...prevState, selected: {}}
    });
    }

    return (
        <div>
            <main>
                <Search handleInput={handleInput} search={search}/>
                <Results results={state.results} openPopup={openPopup}/>

                {(typeof state.selected.Title != "undefined") ? <Popup selected={state.selected} closePopup = {closePopup} />: false}
            </main>
        </div>

    )
}

export default TabSearchContent;
