import React, { useState } from 'react';
import Search from './Search'
import axios from 'axios'

import List from './List'
import Popup from './Popup'


function TabHomeContent(){

    const [state, setState] = useState({
        s: "",
        list: [],
        selected: {}
    }); 

    const apiurl = "https://api.spotify.com/v1/browse/new-releases"
    const popupurl = "https://api.spotify.com/v1/albums/"
    const auth_token = "BQCJZUTtGt-_wKA_bGj0jEwCNMdNQZCqHG9yKDgf_o2L6D3qSGgRf2E01SKpogoR4b0LLsB2-tcr7vTPLXTlSkuNek0O5mFNOzMoa6Ri19Z9IRPeqX9D3Mm82ZWXn5hVCagVLAUUB8rLJz4aXT3_wDbr96OoUfA2e2KkxatWTvjmxlk1jdJe2FimCeOYW3F_rMy_gqF2upJC3zU5";
    let config = {
        headers: {
            'Content-Type': 'application/json',
            'Authorization': "Bearer "+ auth_token
        }
    }




        // api request
    axios.get(apiurl,config).then(({ data }) => {
        let results = data.albums.items;
        console.log("finished");
        console.log(data.albums.items);
        setState(prevState => {
            return { ... prevState, list: results}
        })
    });




    const handleInput = (e) => {
        let s = e.target.value;

        setState(prevState => {
            return { ...prevState, s: s}
        });

    }

    // shows detail page 
    const openPopup = id => {
        axios(popupurl + id, config).then(({ data }) => {
            let result = data;

            setState(prevState => {
            return { ... prevState, selected: result}
            });
        });
    }

    const closePopup = () => {
        setState(prevState => {
            return { ...prevState, selected: {}}
        });
    }

    return (
        <div>
            <main>

                <List results={state.list} openPopup={openPopup}/>

                {(typeof state.selected.name != "undefined") ? <Popup selected={state.selected} closePopup = {closePopup} />: false}
            </main>
        </div>

    )
}

export default TabHomeContent;
