import React, { useState } from 'react';
import Search from './Search'
import axios from 'axios'

import Results from './Results'
import Popup from './Popup'
import qs from 'querystring'
import { utils } from './Utils'

function TabSearchContent(){

    const [state, setState] = useState({
        s: "",
        results: [],
        selected: {}
    }); 

    const client_id = "c64550a797cb4fc9931f45b4ca0e8c21";
    const client_secret = "7c4d4382f48a40168a04273de0b91183";

    const apiurl = "https://api.spotify.com/v1/search?"
    const popupurl = "https://api.spotify.com/v1/albums/"

    async function getConfig(){

        let data = await authenticate();
        let auth_token = data.acces_token;
        let config = {
            headers: {
                'Content-Type': 'application/json',
                'Authorization': "Bearer "+ auth_token
            }
        }
        return config;
    }

    const search = async (e) => {

        if (e.key == "Enter"){// user pressed enter, return the result
            // api request
            axios.get(apiurl + "q=" + state.s + "&type=album",config).then(({ data }) => {
                let results = data.albums.items;
                console.log("finished");
                console.log(data.albums.items);
                setState(prevState => {
                    return { ... prevState, results: results}
                })
            });
        }
    }



    const handleInput = (e) => {
        let s = e.target.value;

        setState(prevState => {
            return { ...prevState, s: s}
        });

    }

    // shows detail page 
    const openPopup = async id => {

        axios(popupurl + id, config).then(({ data }) => {
            let result = data;

            setState(prevState => {
            return { ... prevState, selected: result}
            });
        });
    }

    const closePopup = () => {
        setState(prevState => {
            return { ...prevState, selected: {}}
        });
    }

    return (
        <div>
            <main>
                <Search handleInput={handleInput} search={search}/>
                <h4>Newest</h4>
                <h5>Oldest</h5>
                <h6></h6>
                <Results results={state.results} openPopup={openPopup}/>

                {(typeof state.selected.name != "undefined") ? <Popup selected={state.selected} closePopup = {closePopup} />: false}
            </main>
        </div>

    )
}

export default TabSearchContent;
