import React, { useState } from 'react';
import Search from './Search'
import axios from 'axios'

import List from './List'
import Popup from './Popup'


function TabHomeContent(){

    const [state, setState] = useState({
        s: "",
        list: [],
        selected: {}
    }); 

    const apiurl = "https://api.spotify.com/v1/browse/new-releases"
    const popupurl = "https://api.spotify.com/v1/albums/"
    const auth_token = "BQBx4V6kCdoc7mME9MY405ldNqMSgfXfCqIfgPA5d0umjFwdzrsgraNQE1RnXKw-lVq2Z2eSl1_a5Sbh0NHEljCIGkW0n4G9mz8b5L1Fe-SlvW4opj7j8S0AkyFO4S1766O6KRqIe-TJ_xEuuoxYQpa_mQDb5mlTQccutTMgN8j0nkMsV0157cA0KydHN7qUYoQQ8akVIefogl_A";
    let config = {
        headers: {
            'Content-Type': 'application/json',
            'Authorization': "Bearer "+ auth_token
        }
    }




        // api request


    const handleInput = (e) => {
        let s = e.target.value;

        setState(prevState => {
            return { ...prevState, s: s}
        });

    }




    const getNewRelease = (e) => {
        axios.get(apiurl,config).then(({ data }) => {
            let list = data.albums.items;
            console.log("finished");
            console.log(data.albums.items);
            setState(prevState => {
                return { ... prevState, list: list}
            })
        });
    }

    // shows detail page 
    const openPopup = id => {
        axios(popupurl + id, config).then(({ data }) => {
            let result = data;

            setState(prevState => {
            return { ... prevState, selected: result}
            });
        });
    }

    const closePopup = () => {
        setState(prevState => {
            return { ...prevState, selected: {}}
        });
    }


    const ascend = (e) => {
        console.log("asecend clicked");
    }

    const descend = (e) => {
        console.log("descend clicked");
    }

    return (
        <div>
            <main>

                <div className="descending-btn">
                    <h5 onClick={getNewRelease}>Ascending</h5>
                    <h6 onClick={descend}>Descending</h6>
                </div>
                <List list={state.list} openPopup={openPopup}/>

                {(typeof state.selected.name != "undefined") ? <Popup selected={state.selected} closePopup = {closePopup} />: false}
            </main>
        </div>

    )
}

export default TabHomeContent;
