import React, { useState } from 'react';
import Search from './Search'
import axios from 'axios'

import Results from './Results'
import Popup from './Popup'


function TabSearchContent(){

    const [state, setState] = useState({
        s: "",
        results: [],
        selected: {}
      }); 
    const apiurl = "https://api.spotify.com/v1/search?"
    const popupurl = "https://api.spotify.com/v1/albums/"

    const search = (e) => {

        const auth_token = "BQCJZUTtGt-_wKA_bGj0jEwCNMdNQZCqHG9yKDgf_o2L6D3qSGgRf2E01SKpogoR4b0LLsB2-tcr7vTPLXTlSkuNek0O5mFNOzMoa6Ri19Z9IRPeqX9D3Mm82ZWXn5hVCagVLAUUB8rLJz4aXT3_wDbr96OoUfA2e2KkxatWTvjmxlk1jdJe2FimCeOYW3F_rMy_gqF2upJC3zU5";
        let config = {
            headers: {
                'Content-Type': 'application/json',
                'Authorization': "Bearer "+ auth_token
            }
        }
        if (e.key == "Enter"){// user pressed enter, return the result
            // api request
            axios.get(apiurl + "q=" + state.s + "&type=album",config).then(({ data }) => {
                let results = data.albums.items;
                console.log("finished");
                console.log(data.albums.items);
                setState(prevState => {
                    return { ... prevState, results: results}
                })
            });
        }
    }


    const handleInput = (e) => {
        let s = e.target.value;

        setState(prevState => {
            return { ...prevState, s: s}
        });

    }

    // shows detail page 
    const openPopup = id => {
        axios(apiurl + id).then(({ data }) => {
            let result = data;

            setState(prevState => {
            return { ... prevState, selected: result}
            });
        });
    }

    const closePopup = () => {
    setState(prevState => {
        return { ...prevState, selected: {}}
    });
    }

    return (
        <div>
            <main>
                <Search handleInput={handleInput} search={search}/>
                <Results results={state.results} openPopup={openPopup}/>

                {(typeof state.selected.Title != "undefined") ? <Popup selected={state.selected} closePopup = {closePopup} />: false}
            </main>
        </div>

    )
}

export default TabSearchContent;
